export function obtenerRol(e){return e["WCA ID"]?e.Email.endsWith("@worldcubeassociation.com")?"Delegado":"m"===e.Gender?"Competidor":"Competidora":"m"===e.Gender?"Nuevo competidor":"Nueva competidora"}export function aumentarTamaño(e){var t=document.getElementById(e),o=parseInt(window.getComputedStyle(t).fontSize);o+=1,t.style.fontSize="".concat(o,"px")}export function disminuirTamaño(e){var t=document.getElementById(e),o=parseInt(window.getComputedStyle(t).fontSize);o-=1,t.style.fontSize="".concat(o,"px")}export function eliminarFuente(){for(var e=document.getElementsByTagName("style"),t=0;t<e.length;t++){var o=e[t];(o.textContent||o.innerText).includes("@font-face")&&o.parentNode.removeChild(o)}}export function eliminarPatrocinadores(){document.getElementById("vista-previa").querySelectorAll(".patrocinador").forEach((function(e){e.remove()}))}